// 1) Navegar a la vista de Recetas
cy.get('button[data-view="recipes"]').click();
cy.contains('h2', 'Biblioteca de Recetas', {
  timeout: 10000
}).should('be.visible');
// 2) Componer la receta
cy.get('input[name="recipeName"]').clear().type('Ensalada de Pollo y Manzana');
// Añadir 150g Pechuga de Pollo
cy.get('select[name="ingredientSelect"]').select('Pechuga de Pollo');
cy.get('input[name="grams"]').clear().type('150');
cy.get('#add-ingredient-to-recipe-btn').click();
// Añadir 75g Espinacas Frescas
cy.get('select[name="ingredientSelect"]').select('Espinacas Frescas');
cy.get('input[name="grams"]').clear().type('75');
cy.get('#add-ingredient-to-recipe-btn').click();
// Añadir 50g Manzana Fuji
cy.get('select[name="ingredientSelect"]').select('Manzana Fuji');
cy.get('input[name="grams"]').clear().type('50');
cy.get('#add-ingredient-to-recipe-btn').click();
// Guardar la receta
cy.contains('button', 'Guardar Receta').click();
// 3) Verificar la creación
// Esperar a que la tarjeta de la receta esté visible en la biblioteca y abrirla
cy.contains('[data-action="view-recipe"]', 'Ensalada de Pollo y Manzana', {
  timeout: 20000
}).should('be.visible').click();
// Verificar título en detalle
cy.contains('h2', 'Ensalada de Pollo y Manzana', {
  timeout: 20000
}).should('be.visible');
// Verificar ingredientes con sus pesos usando selectores relativos y timeouts mayores
// Pechuga de Pollo -> 150g
cy.contains('h4', 'Pechuga de Pollo', {
  timeout: 10000
}).should('be.visible').parent().within(() => {
  cy.contains('150g', {
    timeout: 10000
  }).should('exist');
});
// Espinacas Frescas -> 75g
cy.contains('h4', 'Espinacas Frescas', {
  timeout: 10000
}).should('be.visible').parent().within(() => {
  cy.contains('75g', {
    timeout: 10000
  }).should('exist');
});
// Manzana Fuji -> 50g
cy.contains('h4', 'Manzana Fuji', {
  timeout: 10000
}).should('be.visible').parent().within(() => {
  cy.contains('50g', {
    timeout: 10000
  }).should('exist');
});
// Verificar peso por ración total (275g)
cy.get('.text-2xl.font-bold.text-blue-600', {
  timeout: 10000
}).should('contain', '275g');
