// 1) Navegar a la vista de Recetas
cy.get('button[data-view="recipes"]').click();
cy.contains('h2', 'Biblioteca de Recetas', {
  timeout: 10000
}).should('be.visible');
// 2) Componer la receta
cy.get('input[name="recipeName"]').clear().type('Ensalada de Pollo y Manzana');
// Añadir 150g Pechuga de Pollo
cy.get('select[name="ingredientSelect"]').select('Pechuga de Pollo');
cy.get('input[name="grams"]').clear().type('150').should('have.value', '150');
cy.get('#add-ingredient-to-recipe-btn').click();
// ensure the composer shows the added ingredient with the correct grams
cy.get('#recipe-form .max-h-32', {
  timeout: 5000
}).within(() => {
  cy.contains('Pechuga de Pollo').should('be.visible');
  cy.contains('150g').should('be.visible');
});
// Añadir 75g Espinacas Frescas
cy.get('select[name="ingredientSelect"]').select('Espinacas Frescas');
cy.get('input[name="grams"]').clear().type('75').should('have.value', '75');
cy.get('#add-ingredient-to-recipe-btn').click();
cy.get('#recipe-form .max-h-32', {
  timeout: 5000
}).within(() => {
  cy.contains('Espinacas Frescas').should('be.visible');
  cy.contains('75g').should('be.visible');
});
// Añadir 50g Manzana Fuji
cy.get('select[name="ingredientSelect"]').select('Manzana Fuji');
cy.get('input[name="grams"]').clear().type('50').should('have.value', '50');
cy.get('#add-ingredient-to-recipe-btn').click();
cy.get('#recipe-form .max-h-32', {
  timeout: 5000
}).within(() => {
  cy.contains('Manzana Fuji').should('be.visible');
  cy.contains('50g').should('be.visible');
});
// Guardar la receta
cy.contains('button', 'Guardar Receta').click();
// 3) Verificar la creación
// Esperar a que la tarjeta de la receta esté visible en la biblioteca y abrirla
cy.contains('[data-action="view-recipe"]', 'Ensalada de Pollo y Manzana', {
  timeout: 20000
}).should('be.visible').click();
// Verificar en el estado de la app (determinista)
cy.window().its('__getState').should('be.a', 'function').then(getState => {
  const state = getState();
  const recipe = Object.values(state.items.byId).find(i => i.itemType === 'receta' && i.name === 'Ensalada de Pollo y Manzana');
  expect(recipe, 'recipe exists in state').to.exist;
  expect(recipe.ingredients, 'recipe has 3 ingredients').to.have.length(3);
  // verify grams in state
  const chicken = recipe.ingredients.find(it => state.items.byId[it.ingredientId].name === 'Pechuga de Pollo');
  const spinach = recipe.ingredients.find(it => state.items.byId[it.ingredientId].name === 'Espinacas Frescas');
  const apple = recipe.ingredients.find(it => state.items.byId[it.ingredientId].name === 'Manzana Fuji');
  expect(chicken.grams).to.equal(150);
  expect(spinach.grams).to.equal(75);
  expect(apple.grams).to.equal(50);
  // computed totalGrams should be 275
  expect(recipe.computed, 'recipe computed totals').to.exist;
  expect(recipe.computed.totalGrams).to.equal(275);
});
